import React from 'react'
import {useState} from 'react';
import { Row, Col, Image, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { Message } from './Message';


export function CartDropdown() {

    const [isOpen, setIsOpen] = useState(false);

    const dispatch = useDispatch()
    const navigate = useNavigate();

    
    const cart = useSelector(state => state.cart)
    const { userLogin } = useSelector(state => state.userLogin)
    const { userInfo } = userLogin
    const { cartItems } = cart
    
    const checkoutHandler = () => {
        navigate('/cart')
    }

    return (
        <>
             {cartItems.length === 0 ? <Message variant='flush'>No Items in Cart</Message> : 
             <div id="cart-drop-down" className='cart-drop'>
                {cartItems.map(item => (
                    <Row key={item.id} className="cart-row">
                        <Col variant='flush'>
                            <Image src={item.image} alt={item.name} fluid rounded/>
                        </Col>
                        <Col>
                            <p>{item.name}</p>
                        </Col>
                        <Col>
                            <p>${item.price}</p>
                        </Col>
                    </Row>
                ))}
                <Button variant="primary" className="btn btn-primary fluid rounded"
                onClick={checkoutHandler}>CHECKOUT</Button>
            </div>}
        </>
    )
}
