import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'
import { Row, Col, Image, ListGroup, Card, Button, Form} from 'react-bootstrap'
import Rating from '../Components/Rating'
import { listProductDetails, clearProductDetails } from '../actions/productActions';
import { useDispatch, useSelector } from 'react-redux';
import {Loader} from '../Components/Loader';
import {Message} from '../Components/Message';
import { addItemToCart } from '../actions/cartActions'



//params
import { useParams } from 'react-router-dom'


const ProductScreen = ( {  }  ) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [qty, setQty] = useState(1);
    const dispatch = useDispatch();

    const { product, loading, error } = useSelector(state => state.productDetails);

    useEffect(() => {
        dispatch(listProductDetails(id));

        return () => {
            dispatch(clearProductDetails());
        }
    }, [dispatch, id])

    const addToCartHandler = () => {
        //TODO
        //dispatch(addItemToCart(id, qty));
        navigate(`/cart/${id}?qty=${qty}`);
    }

    return (
        <>
            
                <div className="m-20">
                    <Link to="/" className="btn bg-light rounded mb-2 mt-5">Back</Link>
                    {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message> :
                    <Row>
                        <Col md={6}>
                            <Image src={product.image} fluid />
                        </Col>
                        <Col md={3}>
                            <ListGroup variant="flush">
                                <ListGroup.Item>
                                    <h3>{product.name}</h3>
                            </ListGroup.Item>   
                            <ListGroup.Item>
                                <strong>Price: </strong>
                                <span>${product.price}</span>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Rating 
                                value={product.rating}
                                text={`${product.numReviews} reviews`} />
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <strong>Description: </strong>
                                <span>{product.description}</span>
                            </ListGroup.Item>
                            </ListGroup>
                        </Col>
                        <Col md={3}>
                            <Card className="rounded">
                                <ListGroup variant="flush">
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>
                                                Price:
                                            </Col>
                                            <Col>
                                                {product.price}
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>
                                                In Stock:
                                            </Col>
                                            <Col>
                                                {`${product.countInStock}` > 0 ? `${product.countInStock} Items` : 'Out of Stock'}
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                                    {product.countInStock > 0 && (
                                        <ListGroup.Item>
                                            <Row>
                                                <Col>
                                                    Quantity:
                                                </Col>
                                                <Col>
                                                    <Form.Control as="select" value={qty} onChange={(e) => setQty(e.target.value)}>
                                                        {[...Array(product.countInStock).keys()].map(i => (
                                                            <option key={i + 1} value={i + 1}>{i + 1}</option>
                                                        ))}
                                                    </Form.Control>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    )}
                                    <Button 
                                    onClick={addToCartHandler}
                                    className="btn-block m-2 rounded" 
                                    variant="success" type="button" 
                                    disabled={product.countInStock === 0}>
                                        Add to Cart
                                    </Button>
                                </ListGroup>
                            </Card>
                        </Col>
                    </Row>
                    }
            </div>
        </>
        
    )
}

export default ProductScreen
